import './App.css';
import React, { useState } from 'react';

function App() {
  let todo = {
    id:0,
    title: '리액트 과제 제출하기!',
    body: '꼭 제출하자..',
    isDone: false
  }
  const [title, SetTitle] = useState('')
  const [body, SetBody] = useState('')
  const [todos, SetTodos] = useState([todo2])

  function iptChangeEventHandler(e){
    if(e.target.id === 'titleIpt'){
      SetTitle(e.target.value)
    }else{
      SetBody(e.target.value)
    }
  }

  function addBtnClickEventHandler(e){
    todos.push({id:todos.length, title:title, body:body, isDone : false})
    SetTodos([...todos])
    SetTitle('')
    SetBody('')
    console.log(todos)
  }

  return (
    <div className="App">
      <div className="App-Header">MY TODO LIST</div>
      <div className="create-div">
        <label for="titleIpt">TASK</label><input id="titleIpt" type="text" onChange={iptChangeEventHandler} value={title}></input>
        <label for="badyIpt">TASK CONTENT</label><input id="bodyIpt" type="text" onChange={iptChangeEventHandler} value={body}></input>
        <button onClick={addBtnClickEventHandler}>추가</button>
      </div>
      <div className="select-div">
        <div className="header">Working</div>
        <div className="cards working">
          <Todos todos={todos} todoState="working"/>
        </div>

        <div className="header">Done</div>
        <div className="cards done">
          <Todos todos={todos} todoState="done"/>
        </div>

      </div>
    </div>
  );
}

function Todos(props){
  if(props.todoState =="working"){
    return (
      <>
          {
            props.todos.filter((todo)=>{
              return todo.isDone == false
            }).map((todo, idx)=>{
              return (<div key ={todo.id} className="task">
              <div className="task-title">{todo.title}</div>
              <div className="task-body">{todo.body}</div>
              <div className="task-btns"><button id={"del"+todo.id}>삭제</button><button id={"done"+todo.id}>완료</button></div>
            </div>

              )
            })
          }
      </>
      )
  }else{
    return (
      <>
          {
            props.todos.filter((todo)=>{
              return todo.isDone == true
            }).map((todo, idx)=>{
              return (<div key ={todo.id} className="task">
              <div className="task-title">{todo.title}</div>
              <div className="task-body">{todo.body}</div>
              <div className="task-btns"><button id={"del"+todo.id}>삭제</button><button id={"done"+todo.id}>취소</button></div>
            </div>

              )
            })
          }
      </>
      )
  }
}

export default App;
