import React from 'react';
import { useNavigate, useParams } from "react-router-dom";
import { useSelector, useDispatch } from 'react-redux';
import { selectTodo} from '../redux/modules/todoReduser'
import styled from "styled-components";
const StApp = styled.div`
  min-width: 800px;
  max-width: 1200px;
  background-color: #282c34;
  margin : 0 auto;
  color : whitesmoke;
`
const StDiv = styled.div`
  background-color: #282c34;
  height: 50px;
  color: #61dafb;
  font-size: large;
  font-weight: 900;
  display: flex;
  align-items : center;
  margin: 0;
  justify-content: center;
  color :whitesmoke
`
const StBtn = styled.button`
  border: solid 3px blueviolet;
  background-color: blueviolet;
  font-size: large;
  text-decoration: none;
  border-radius: 8px;
  width: 100px;
  padding: 5px;
  margin: 10px 30px 10px 10px;
  color: white;
  &:hover {
    border: solid 3px white;
    background-color: white; 
    color: #282c34;
  } 
`

function Details() {
    const navigate = useNavigate();
    const params = useParams();

    const todoList = useSelector((state) => {
        return state.todoReducer.todoList
    })

    const dispatch = useDispatch()
    dispatch(selectTodo())

    const todo = todoList.filter((todo)=>{
        return todo.id == params.id
    })


    return (
        <StApp>
            <StBtn onClick={() => { navigate(-1) }} >이전으로</StBtn>
            <StDiv><h2>글번호 : </h2>{params.id}</StDiv>
            <StDiv>제목{todo[0].title}</StDiv>
            <StDiv>내용{todo[0].body}</StDiv>
        </StApp>
    );
}

export default Details;
