// 액션 타입
const CRATE_TODO = "CRATE_TODO"     // TODO 추가
const DELETE_TODO = "DELETE_TODO"   // TODO 삭제
const UPDATE_TODO = "UPDATE_TODO"   // TODO 수정 (제목, 내용, 상태)
const SELECT_TODO = "SELECT_TODO"   // TODO 상세보기

// ActionCreater
export const crateTodo = (data) => {
    console.log("6. crateTodo 액션 생성자", data)
    return {
        type: CRATE_TODO,
        payload: data
    }
}

export const deleteTodo = (data) => {
    console.log("8. data", data)
    console.log("9. data.id", data.id)
    return {
        type: DELETE_TODO,
        payload : data,
    }
}

export const updateTodo = (data) => {
    console.log("4. updateTodo 액션 생성자", data)
    console.log("4.1. updateTodo 액션 생성자 title>>", data.title)
    console.log("4.2. updateTodo 액션 생성자 body>>", data.body)

    return {
        type: UPDATE_TODO,
        payload: data
    }
}

export const selectTodo = (payload) => {
    return {
        type: SELECT_TODO,
        payload : Number(payload)
    }
}

// 초기상태값
const initState = {
    todo : {
        id: 0,
        isDone: false,
        title:'',
        body:''
    },
    todoList : []
}

/**
 * 리듀서 : state를 액션의 타입에 맞추어서 변화를 일으키는 함수
 * 액션 : state를 어떻게 변화시킬지 타입과 내용을 기록한것
 * @param {} initState 
 * @param {} action 
 */


const todoReducer = (state = initState, action) => {
    console.log("0. state:",state, ", state.id:",state.id)
    switch (action.type) {
        case CRATE_TODO:
            console.log("6. TODO 추가")
            return {
                todo : {
                    id: state.todo.id + 1,
                    isDone: false,
                    title: '',
                    body: ''
                },
                todoList : [...state.todoList, state.todo]
            }
        case DELETE_TODO:
            console.log("7. TODO 삭제")
            console.log("7.1 payload", action.payload)
            console.log("7.2 todoList", state.todoList)

            state.todoList = state.todoList.filter((x)=>{
                return x.id != action.payload
            })

            console.log("7.2 todoList", state.todoList)
            console.log("7.2 after[]> ", state.todoList)
            return {
                todo : {
                    id: state.todo.id,
                    isDone: false,
                    title: '',
                    body: ''
                },
                todoList : []
            }
        case UPDATE_TODO:
            console.log("5. TODO 수정 (제목, 내용, 상태)")
            console.log("5.1. state >>",state)
            console.log("5.2. action >>",action)
            console.log("5.3. action.payload.title >>",action.payload.title)
            console.log("5.4. action.payload.body >>",action.payload.body)
            return {
                todo : {
                    id: state.todo.id,
                    isDone: state.todo.isDone,
                    title: action.payload.title,
                    body: action.payload.body
                },
                todoList : state.todoList
            }
        case SELECT_TODO:
            console.log("// TODO 상세보기")
            return {
                id: state.id+1,
                isDone: false,
                title:'',
                body:''
            }
        default:
            return {
                todo : {
                    id: state.todo.id,
                    isDone: false,
                    title: '',
                    body: ''
                },
                todoList : []
            }




    }
}

export default todoReducer
