// 액션 타입
const CRATE_TODO = "CRATE_TODO"     // TODO 추가
const DELETE_TODO = "DELETE_TODO"   // TODO 삭제
const UPDATE_TODO = "UPDATE_TODO"   // TODO 수정 (제목, 내용, 상태)
const SELECT_TODO = "SELECT_TODO"   // TODO 상세보기

// ActionCreater
export const crateTodo = () => {
    return {
        type: CRATE_TODO
    }
}

export const deleteTodo = () => {
    return {
        type: DELETE_TODO
    }
}

export const updateTodo = (str) => {
    console.log("str >",str)
    return {
        type: UPDATE_TODO,
        payload: str

    }
}

export const selectTodo = (payload) => {
    return {
        type: SELECT_TODO,
        payload : Number(payload)
    }
}

// 초기상태값
const initState = {
    id: 0,
    isDone: false,
    title:'',
    body:''
}

/**
 * 리듀서 : state를 액션의 타입에 맞추어서 변화를 일으키는 함수
 * 액션 : state를 어떻게 변화시킬지 타입과 내용을 기록한것
 * @param {} initState 
 * @param {} action 
 */


const todoReducer = (state = initState, action) => {
    switch (action.type) {
        case CRATE_TODO:
            console.log("// TODO 추가")
            return {
                id: state.id+1,
                isDone: false,
                title:'',
                body:''
            }
        case DELETE_TODO:
            console.log("// TODO 삭제")
            return {
                state
            }
        case UPDATE_TODO:
            console.log("// TODO 수정 (제목, 내용, 상태)")
            console.log(":: todoReducer in state >>",state.state.title)
            console.log(":: 입력문자열 >>",state.state.title + action.payload.target.value)
            return {
                id: state.state.id,
                isDone: false,
                title:state.state.title + action.payload.target.value,
                body:state.state.body + action.payload.target.value,
            }
        case SELECT_TODO:
            console.log("// TODO 상세보기")
            return {
                state
            }
        default:
            return {
                state
            }
    }
}

export default todoReducer
